import Worker from 'worker-loader!./workers/wallet.worker.js'

const resolves = {}
const rejects = {}
let globalMsgId = 0

/**
 * Class Wrapper
 * @class
 *
 * Wrapper for communicating with Web Workers, using Promises
 */

export default class Wrapper {
  constructor () {
    this.worker = new Worker()
    this.worker.onmessage = handleMsg
  }

  /**
   * Main class method. Send message to the worker
   * @param {string} method - Method name
   * @param {Object} value - Data set for a specific method
   * @returns {*|Promise<Object>} - An object with data that depends on a specific method
   */
  method (method, value) {
    return sendMsg({method, value}, this.worker)
  }
}

/**
 * Activate calculation in the worker, returning a promise
 * @param {Object} payload
 * @param {worker} worker
 * @returns {Promise<Object>}
 */

function sendMsg (payload, worker) {
  const msgId = globalMsgId++

  const msg = {
    id: msgId,
    payload
  }

  return new Promise(function (resolve, reject) {
    resolves[msgId] = resolve
    rejects[msgId] = reject
    worker.postMessage(msg)
  })
}

/**
 * Handle incoming calculation result
 * @param {Object} msg
 */

function handleMsg (msg) {
  const {id, error, payload} = msg.data
  if (error) {
    const reject = rejects[id]
    if (reject) {
      if (error) {
        reject(error)
      } else {
        reject('Got nothing')
      }
    }
  } else if (payload) {
    const resolve = resolves[id]
    if (resolve) {
      resolve(payload)
    }
  }
}

import converter from '@/helpers/converters'
import {calcBtcTxSize, getBtcPrivateKeyByIndex, makeRawBchTx} from '@/helpers/coreHelper'
import CustomError from '@/helpers/handleErrors'

/**
 * List of available commission types for Bitcoin Cash transactions
 * @type {Array}
 */

const FEE_IDS = ['regular', 'custom']

/**
 * Class BitcoinCashTx.
 * This class is responsible for calculating the fee,
 * calculating the available amount to send, and generating and signing a Bitcoin Cash transaction
 * @class
 */

export default class BitcoinCashTx {
  /**
   * Create a BitcoinCashTx
   * @param {Object} data - Input data for generating a transaction, calculating a fee or available amount
   * @param {Array} data.unspent - Array of unspent addresses
   * @param {number} data.amount - Transaction amount
   * @param {number} data.balance - Bitcoin Cash wallet balance
   * @param {Array} data.feeList - Set of raw Bitcoin Cash fees
   * @param {Object} data.customFee - Custom fee entered by the user
   * @param {Object} data.nodes - External and internal Bitcoin Cash nodes
   * @param {string} data.internalAddress - Address for change
   */
  constructor (data) {
    this.unspent = data.unspent
    this.amount = data.amount ? converter.btc_to_sat(data.amount) : 0
    this.balance = data.balance
    this.customFee = +data.customFee ? +data.customFee : 0
    this.nodes = data.nodes
    this.internalAddress = data.internalAddress
    this.fee = data.feeList
    this.feeList = []
    this.dust = 1000
  }

  /**
   * Calculating the fee amount
   * @param {number} size - Transaction size
   * @returns {Promise<Array>} Returns a set of fees for a specific transaction amount
   */

  async calcFee (size = 0) {
    const fees = [...this.fee.map(item => item.feePerByte), this.customFee]

    if (this.amount <= 0 || this.balance < this.amount) {
      return this.calcEmptyFee(fees)
    }

    const pArray = fees.map(async fee => {
      return await this.getInputs(fee, size)
    })

    const res = await Promise.all(pArray)

    this.feeList = res.map((item, i) => {
      return {
        id: FEE_IDS[i],
        SAT: item.fee,
        BCH: converter.sat_to_btc(item.fee),
        fee: fees[i],
        feeInBTC: converter.sat_to_btc(fees[i]),
        inputs: item.inputs,
        inputsAmount: item.inputsAmount,
        custom: FEE_IDS[i] === 'custom'
      }
    })

    return this.feeList
  }

  /**
   * Sets an array of zero fees.
   * Used when the user does not have enough funds for the transaction
   * @param {Array} fees - set of commission types
   * @returns {Array} Returns an array with zero fees
   */

  calcEmptyFee (fees) {
    this.feeList = fees.map((item, i) => {
      return {
        id: FEE_IDS[i],
        SAT: 0,
        BCH: 0,
        fee: item,
        feeInBTC: converter.sat_to_btc(item),
        inputs: [],
        inputsAmount: 0,
        custom: FEE_IDS[i] === 'custom'
      }
    })

    return this.feeList
  }

  /**
   * Finds a list of inputs for a specific transaction
   * @param {number} fee - Fee size
   * @param {number} size - Transaction size
   * @returns {Promise<Object>} Returns an object with a list of inputs, the total fee amount, and the total amount of all inputs
   */

  async getInputs (fee, size) {
    let index = 0
    let inputsAmount = 0
    let inputs = []
    let res = {}

    this.dust = size ? 0 : 1000

    let req = async () => {
      let item = this.unspent[index]
      let defaultSize = calcBtcTxSize(index + 1, 2)
      let calcFee = size ? size * fee : defaultSize * fee

      inputsAmount += item.satoshis
      inputs.push(item)

      let total = this.amount + calcFee + this.dust

      if (total > inputsAmount) {
        index++

        if (index >= this.unspent.length) {
          res = {
            fee: 0,
            inputs: [],
            inputsAmount: 0
          }
        } else {
          await req()
        }
      } else {
        res = {
          fee: calcFee,
          inputs: inputs,
          inputsAmount: inputsAmount
        }
      }
    }
    await req()

    return res
  }

  /**
   * Creating a Bitcoin Cash transaction
   * @param {Object} data - Input data for a transaction
   * @param {string} data.addressTo - Recipient address
   * @param {Object} data.fee - The transaction fee and list of inputs
   * @returns {Promise<Object>} Returns the raw transaction and transaction hash if sent successfully
   */

  async make (data) {
    const {addressTo, fee} = data

    if (!this.amount) {
      throw new CustomError('err_tx_bch_amount')
    }

    if (isNaN(fee.SAT)) {
      throw new CustomError('err_tx_bch_fee')
    }

    const inputsAmount = +fee.inputsAmount
    const amount = +this.amount
    const sat = +fee.SAT
    const change = inputsAmount - amount - sat
    let inputs = []

    for (const utxo of fee.inputs) {
      let item = {
        txId: utxo.txid,
        outputIndex: utxo.vout,
        address: utxo.cashAddress,
        script: utxo.scriptPubKey,
        satoshis: utxo.satoshis,
        key: getBtcPrivateKeyByIndex(this.nodes[utxo.nodeType], utxo.deriveIndex)
      }

      inputs.push(item)
    }

    if (change >= 0) {
      let params = {
        inputs: inputs,
        outputs: [
          {
            address: addressTo,
            satoshis: amount
          }
        ]
      }

      if (change !== 0) {
        params.outputs[1] = {
          address: this.internalAddress,
          satoshis: change
        }
      }
      return makeRawBchTx(params)
    } else {
      throw new CustomError('err_tx_bch_balance')
    }
  }
}

import converter                                              from '@/helpers/converters'
import {calcBtcTxSize, getBtcPrivateKeyByIndex, makeRawBtcTx} from '@/helpers/coreHelper'
import CustomError                                            from '@/helpers/handleErrors'
import Request                                                from '@/helpers/Request'

/**
 * List of available commission types for Bitcoin transactions
 * @type {Array}
 */

const FEE_IDS = ['fast', 'regular', 'cheap', 'custom']

/**
 * Class BitcoinTx.
 * This class is responsible for calculating the fee,
 * calculating the available amount to send, and generating and signing a Bitcoin transaction
 * @class
 */

export default class BitcoinTx {
  /**
   * Create a BitcoinTx
   * @param {Object} data - Input data for generating a transaction, calculating a fee or available amount
   * @param {Array} data.unspent - Array of unspent addresses
   * @param {string} data.internalAddress - Address for change
   * @param {number} data.amount - Transaction amount
   * @param {number} data.balance - Bitcoin wallet balance
   * @param {Array} data.feeList - Set of bitcoin fees
   * @param {Object} data.customFee - Custom fee entered by the user
   * @param {Object} data.nodes - External and internal nodes required to generate private keys
   * @param {String} data.type - Bitcoin type. There may be p2pkh or p2wpkh
   */

  constructor (data) {
    this.unspent = data.unspent
    this.internalAddress = data.internalAddress
    this.amount = data.amount ? converter.btc_to_sat(data.amount) : 0
    this.balance = data.balance
    this.dust = 1000
    this.fee = data.feeList
    this.customFee = +data.customFee || 0
    this.nodes = data.nodes || {}
    this.feeList = []
    this.request = new Request(data.api)
    this.type = data.type ? data.type.toLowerCase() : 'p2pkh'
  }

  /**
   * Calculating the fee amount
   * @param {number} size - Transaction size
   * @returns {Promise<Array>} Returns a set of fees for a specific transaction amount
   */

  async calcFee (size = 0) {
    const fees = [...this.fee.map(item => item.feePerByte), this.customFee]

    if (this.amount <= 0 || this.balance < this.amount) {
      return this.calcEmptyFee(fees)
    }

    const pArray = fees.map(async fee => {
      return await this.getInputs(fee, size)
    })

    const res = await Promise.all(pArray)

    this.feeList = res.map((item, i) => {
      return {
        id: FEE_IDS[i],
        SAT: item.fee,
        BTC: converter.sat_to_btc(item.fee),
        fee: fees[i],
        feeInBTC: converter.sat_to_btc(fees[i]),
        inputs: item.inputs,
        inputsAmount: item.inputsAmount,
        custom: FEE_IDS[i] === 'custom'
      }
    })

    return this.feeList
  }

  /**
   * Sets an array of zero fees.
   * Used when the user does not have enough funds for the transaction
   * @returns {Array} Returns an array with zero fees
   */

  calcEmptyFee (fees) {
    this.feeList = fees.map((item, i) => {
      return {
        id: FEE_IDS[i],
        SAT: 0,
        BTC: 0,
        fee: item,
        feeInBTC: converter.sat_to_btc(item),
        inputs: [],
        inputsAmount: 0,
        custom: FEE_IDS[i] === 'custom'
      }
    })

    return this.feeList
  }

  /**
   * Finds a list of inputs for a specific transaction
   * @param {number} fee - Fee size
   * @param {number} size - Transaction size
   * @returns {Promise<Object>} Returns an object with a list of inputs, the total fee amount, and the total amount of all inputs
   */

  async getInputs (fee, size) {
    let index = 0
    let inputsAmount = 0
    let inputs = []
    let res = {}

    this.dust = size ? 0 : 1000

    let req = async () => {
      let item = this.unspent[index]
      let defaultSize = calcBtcTxSize(index + 1, 2, this.type === 'p2wpkh')
      let calcFee = size ? size * fee : defaultSize * fee

      inputsAmount += item.value
      inputs.push(item)

      let total = this.amount + calcFee + this.dust

      if (total > inputsAmount) {
        index++

        if (index >= this.unspent.length) {
          res = {
            fee: 0,
            inputs: [],
            inputsAmount: 0
          }
        } else {
          await req()
        }
      } else {
        res = {
          fee: calcFee,
          inputs: inputs,
          inputsAmount: inputsAmount
        }
      }
    }
    await req()

    return res
  }

  /**
   * Creating a Bitcoin transaction
   * @param {Object} data - Input data for a transaction
   * @param {string} data.addressTo - Recipient address
   * @param {Object} data.fee - The transaction fee and list of inputs
   * @returns {Promise<Object>} Returns the raw transaction and transaction hash if sent successfully
   */

  async make (data) {
    const {addressTo, fee} = data

    if (!this.amount) {
      throw new CustomError('err_tx_btc_amount')
    }

    if (isNaN(fee.SAT)) {
      throw new CustomError('err_tx_btc_fee')
    }

    let change = +fee.inputsAmount - +this.amount - +fee.SAT
    let inputs = []

    try {
      inputs = await this.getInputsWithTxInfo(fee.inputs)
    }
    catch (e) {
      throw new Error(e.message)
    }

    if (change >= 0) {
      let params = {
        inputs: inputs,
        outputs: [
          {
            address: addressTo,
            value: this.amount
          }
        ]
      }

      if (change) {
        params.outputs[1] = {
          address: this.internalAddress,
          value: change
        }
      }

      return makeRawBtcTx(params)
    } else {
      throw new CustomError('err_tx_btc_balance')
    }
  }

  /**
   * Returns the required data to create a transaction
   * @param {Array} inputs - Array of inputs for tx
   * @returns {Promise<Object>} Returns an array of inputs with a private keys and raw transaction data for p2pkh items
   */

  async getInputsWithTxInfo (inputs) {
    try {
      let rawTxsData = []

      if (this.type === 'p2pkh') {
        let hashes = []

        for (let input of inputs) {
          if (!input.tx) {
            if (input.tx_hash_big_endian) {
              hashes.push(input.tx_hash_big_endian)
            } else {
              throw new CustomError('err_tx_btc_unspent')
            }
          }
        }
        const unique_hashes = [...new Set(hashes)]

        rawTxsData = await this.getRawTxHex(unique_hashes)

        for (let input of inputs) {
          if (!input.tx) {
            let data = rawTxsData.find(item => item.hash === input.tx_hash_big_endian)
            input.tx = data ? data.rawData : null
          }
          input.key = input.key || getBtcPrivateKeyByIndex(this.nodes[input.node_type], input.derive_index)
        }
      } else {
        for (let input of inputs) {
          input.key = input.key || getBtcPrivateKeyByIndex(this.nodes[input.node_type], input.derive_index)
        }
      }
    }
    catch (e) {
      throw new Error(e.message)
    }
    return inputs
  }

  /**
   * Raw transaction request
   * @param {Array} hashes - List of hashes
   * @returns {Promise<Array>} Array of raw Bitcoin transactions for each hash
   */

  async getRawTxHex (hashes) {
    if (!hashes || !hashes.length) return []

    const ARRAY_SIZE = 10
    const ARRAYS_COUNT = Math.ceil(hashes.length / ARRAY_SIZE)
    let txs = []
    let arrays = []
    let counter = 0

    for (let i = 0; i < ARRAYS_COUNT; i++) {
      arrays[i] = hashes.slice((i * ARRAY_SIZE), (i * ARRAY_SIZE) + ARRAY_SIZE)
    }

    const req = async () => {
      try {
        let res = await this.request.send({
          method: 'rawtx',
          txs: arrays[counter]
        })

        if (res.status === 'success' && res.data.length) {
          txs = [...txs, ...res.data]
          counter++

          if (counter !== ARRAYS_COUNT) {
            await req()
          }
        } else {
          throw new CustomError('err_tx_btc_raw_tx')
        }
      }
      catch (e) {
        throw new CustomError('err_tx_btc_raw_tx')
      }
    }

    await req()

    return txs
  }
}

import converter from '@/helpers/converters'
import {calcBtcTxSize, getBtcPrivateKeyByIndex, makeRawBtcvTx} from '@/helpers/coreHelper'
import CustomError from '@/helpers/handleErrors'

/**
 * List of available commission types for Bitcoin Vault transactions
 * @type {Array}
 */

const FEE_IDS = ['regular', 'custom']

/**
 * Class BitcoinVaultTx.
 * This class is responsible for calculating the fee,
 * calculating the available amount to send, and generating and signing a Bitcoin Vault transaction
 * @class
 */

export default class BitcoinVaultTx {
  /**
   * Create a BitcoinVaultTx
   * @param {Object} data - Input data for generating a transaction, calculating a fee or available amount
   * @param {Array} data.unspent - Array of unspent addresses
   * @param {number} data.amount - Transaction amount
   * @param {number} data.balance - Bitcoin Vault wallet balance
   * @param {Array} data.feeList - Set of raw Bitcoin Vault fees
   * @param {Object} data.customFee - Custom fee entered by the user
   * @param {Object} data.nodes - External and internal Bitcoin Vault nodes
   * @param {string} data.internalAddress - Address for change
   */
  constructor (data) {
    this.unspent = data.unspent
    this.amount = data.amount ? converter.btc_to_sat(data.amount) : 0
    this.balance = data.balance
    this.customFee = +data.customFee ? +data.customFee : 0
    this.nodes = data.nodes
    this.internalAddress = data.internalAddress
    this.fee = data.feeList
    this.feeList = []
    this.dust = 1000
  }

  /**
   * Calculating the fee amount
   * @param {number} size - Transaction size
   * @returns {Promise<Array>} Returns a set of fees for a specific transaction amount
   */

  async calcFee (size = 0) {
    const fees = [...this.fee.map(item => item.feePerByte), this.customFee]

    if (this.amount <= 0 || this.balance < this.amount) {
      return this.calcEmptyFee(fees)
    }

    const pArray = fees.map(async fee => {
      return await this.getInputs(fee, size)
    })

    const res = await Promise.all(pArray)

    this.feeList = res.map((item, i) => {
      return {
        id: FEE_IDS[i],
        SAT: item.fee,
        BTCV: converter.sat_to_btc(item.fee),
        fee: fees[i],
        feeInBTC: converter.sat_to_btc(fees[i]),
        inputs: item.inputs,
        inputsAmount: item.inputsAmount,
        custom: FEE_IDS[i] === 'custom'
      }
    })

    return this.feeList
  }

  /**
   * Sets an array of zero fees.
   * Used when the user does not have enough funds for the transaction
   * @param {Array} fees - set of commission types
   * @returns {Array} Returns an array with zero fees
   */

  calcEmptyFee (fees) {
    this.feeList = fees.map((item, i) => {
      return {
        id: FEE_IDS[i],
        SAT: 0,
        BTCV: 0,
        fee: item,
        feeInBTC: converter.sat_to_btc(item),
        inputs: [],
        inputsAmount: 0,
        custom: FEE_IDS[i] === 'custom'
      }
    })

    return this.feeList
  }

  /**
   * Finds a list of inputs for a specific transaction
   * @param {number} fee - Fee size
   * @param {number} size - Transaction size
   * @returns {Promise<Object>} Returns an object with a list of inputs, the total fee amount, and the total amount of all inputs
   */

  async getInputs (fee, size) {
    let index = 0
    let inputsAmount = 0
    let inputs = []
    let res = {}

    this.dust = size ? 0 : 1000

    let req = async () => {
      let item = this.unspent[index]
      let defaultSize = calcBtcTxSize(index + 1, 2, true)
      let calcFee = size ? size * fee : defaultSize * fee

      inputsAmount += item.value
      inputs.push(item)

      let total = this.amount + calcFee + this.dust

      if (total > inputsAmount) {
        index++

        if (index >= this.unspent.length) {
          res = {
            fee: 0,
            inputs: [],
            inputsAmount: 0
          }
        } else {
          await req()
        }
      } else {
        res = {
          fee: calcFee,
          inputs: inputs,
          inputsAmount: inputsAmount
        }
      }
    }
    await req()

    return res
  }

  /**
   * Creating a Bitcoin Vault transaction
   * @param {Object} data - Input data for a transaction
   * @param {string} data.addressTo - Recipient address
   * @param {Object} data.fee - The transaction fee and list of inputs
   * @returns {Promise<Object>} Returns the raw transaction and transaction hash if sent successfully
   */

  async make (data) {
    const {addressTo, fee} = data

    if (!this.amount) {
      throw new CustomError('err_tx_btcv_amount')
    }

    if (isNaN(fee.SAT)) {
      throw new CustomError('err_tx_btcv_fee')
    }

    const inputsAmount = +fee.inputsAmount
    const amount = +this.amount
    const feeSat = +fee.SAT
    const change = inputsAmount - amount - feeSat
    let inputs = []

    for (const utxo of fee.inputs) {
      let item = {
        hash: utxo.tx_hash,
        index: utxo.tx_pos,
        value: utxo.value,
        key: getBtcPrivateKeyByIndex(this.nodes[utxo.nodeType], utxo.deriveIndex)
      }

      inputs.push(item)
    }

    if (change >= 0) {
      let params = {
        inputs: inputs,
        outputs: [
          {
            address: addressTo,
            value: amount
          }
        ]
      }

      if (change !== 0) {
        params.outputs[1] = {
          address: this.internalAddress,
          value: change
        }
      }

      return makeRawBtcvTx(params)
    } else {
      throw new CustomError('err_tx_btcv_balance')
    }
  }
}

import Request from '@/helpers/Request'

/**
 * Class EthereumSync
 * This class allows you to get information about the balance on a ethereum wallet,
 * the list of transactions and optimal gas price
 * @class
 */

export default class EthereumSync {
  /**
   * Create a EthereumSync
   * @param {string} address - Ethereum wallet address
   * @param {Object} api - A set of URLs for getting information about ethereum address
   */
  constructor (address, api) {
    this.address = address
    this.api = api
    this.balance = 0
    this.transactions = []
    this.gasPrice = 0
    this.request = new Request(this.api.eth)
  }

  /**
   * The method that starts the synchronization Ethereum part of the wallet
   * @returns {Promise<number>}
   * @constructor
   */

  async Start () {
    this.balance = await this.getBalance()
    this.transactions = await this.getTransactions()
    this.gasPrice = await this.getGasPrice()
  }

  /**
   * Request to receive a balance of Ethereum wallet
   * @returns {Promise<number>}
   */

  async getBalance () {
    this.balance = 0

    let params = {
      module: 'account',
      action: 'balance',
      address: this.address,
      tag: 'latest'
    }

    let res = await this.request.send(params)

    return res && res.hasOwnProperty('result') && !isNaN(res.result) ? +res.result : 0
  }

  /**
   * Request to receive Ethereum transaction list
   * @returns {Promise<Array>}
   */

  async getTransactions () {
    this.transactions = []

    let params = {
      module: 'account',
      action: 'txlist',
      address: this.address,
      sort: 'asc'
    }

    let res = await this.request.send(params)

    return res && res.hasOwnProperty('result') && Array.isArray(res.result) ? res.result : []
  }

  /**
   * Request to receive a amount of gas price
   * @returns {Promise<number>}
   */

  async getGasPrice () {
    let params = {
      module: 'proxy',
      action: 'eth_gasPrice'
    }

    let res = await this.request.send(params)

    return res && res.hasOwnProperty('result') ? parseInt(res.result, 16) : 0
  }

  get DATA () {
    return {
      address: this.address,
      balance: this.balance,
      transactions: this.transactions,
      gasPrice: this.gasPrice
    }
  }
}

import converter from '@/helpers/converters'
import toFormatDecimal from '@/helpers/toFormatDecimal'
import {makeRawEthTx} from '@/helpers/coreHelper'
import CustomError from '@/helpers/handleErrors'

/**
 * Class EthereumTx.
 * This class is responsible for calculating the fee and generating and signing a Ethereum transaction
 * @class
 */

export default class EthereumTx {
  /**
   * Create a EthereumTx
   * @param {Object} data - Input data for generating a transaction or calculating a fee
   * @param {string} data.address - Ethereum wallet address
   * @param {Array} data.privateKey - Ethereum private key in Uint8Array format
   * @param {number} data.balance - Ethereum wallet balance
   * @param {number} data.gasPrice - Gas price for transaction
   */
  constructor (data) {
    this.address = data.address
    this.privateKey = data.privateKey
    this.balance = data.balance
    this.gasPrice = data.gasPrice
    this.defaultGasLimit = 21000
    this.feeList = []
  }

  /**
   * Calculating the fee amount
   * @param {number} customGasPrice - Amount of custom gas price
   * @param {number} customGasLimit - Amount of custom gas limit
   * @returns {Array} A list with the optimal and custom fee
   */

  calcFee (customGasPrice = 0, customGasLimit = 0) {
    this.feeList = [
      {
        id: 'optimal',
        gasPrice: this.gasPrice,
        gasLimit: this.defaultGasLimit,
        fee: toFormatDecimal(
          converter.wei_to_eth(this.gasPrice * this.defaultGasLimit)
        )
      },
      {
        custom: true,
        id: 'custom',
        gasPrice: customGasPrice,
        gasLimit: customGasLimit,
        fee: toFormatDecimal(
          converter.wei_to_eth(customGasPrice * customGasLimit)
        )
      }
    ]

    return this.feeList
  }

  /**
   * Creating a transaction
   * @param {Object} data - Input data for a transaction
   * @param {string} data.addressTo - Recipient address
   * @param {number} data.amount - Transaction amount in ETH
   * @param {Object} data.fee - Object with amount of gas price and gas limit
   * @param {number} data.nonce - Nonce, transaction count of an account
   * @returns {Promise<Object>} Return a raw transaction in hex to send and transaction hash
   */

  async make (data) {
    const {addressTo, value, fee, nonce} = data
    const amountInWei = converter.eth_to_wei(value)
    const surrender = this.balance - (amountInWei + fee.gasPrice * fee.gasLimit)

    if (surrender < 0) {
      throw new CustomError('err_tx_eth_balance')
    }

    let params = {
      to: addressTo,
      value: amountInWei,
      nonce,
      gasPrice: fee.gasPrice,
      gasLimit: fee.gasLimit,
      privateKey: this.privateKey
    }

    return makeRawEthTx(params)
  }

  get DATA () {
    return {
      fee: this.feeList
    }
  }
}

import Core from '@/class/Core'
import BitcoinSync from '@/class/BTC/BitcoinSync'
import EthereumSync from '@/class/ETH/EthereumSync'
import BitcoinCashSync from '@/class/BCH/BitcoinCashSync'
import BitcoinVaultSync from '@/class/BTCV/BitcoinVaultSync'
import BitcoinTx from '@/class/BTC/BitcoinTx'
import EthereumTx from '@/class/ETH/EthereumTx'
import BitcoinCashTx from '@/class/BCH/BitcoinCashTx'
import BitcoinVaultTx from '@/class/BTCV/BitcoinVaultTx'

/**
 * Class WalletWrapper
 * @class
 */

export default class WalletWrapper {
  /**
   * Create a wallet wrapper
   * @param {Object} params
   * @param {Object} params.data - Input information for creating the core.
   * Contains a method for the creation and optionally a key or mnemonics
   * @param {Object} params.api - A set of URLs for getting information about addresses
   */

  constructor (params) {
    this.data = params.data
    this.api = params.api
    this.core = null
    this.sync = {
      BTC: {},
      ETH: {},
      BCH: null
    }
  }

  /**
   * Creating a wallet core
   * @constructor
   */

  async Create () {
    try {
      this.core = new Core(this.data)
      await this.core.generateWallet()
    }
    catch (e) {
      throw new Error(e.message)
    }
  }

  /**
   * Creating a core for each supported currency type
   *
   * @param {Array<{coin: String, type: String}>} coins
   * @param {string} coins[].coin - Short name of coin. Supported coins are BTC, ETH, BCH and BTCV
   * @param {string|number} coins[].type - Coin type (additional).
   * For BTC supported types are p2pkh and p2wpkh. For ETH type is a account number (by default 0).
   * */

  async CreateCoins (coins) {
    try {
      return await this.core.createCoinsCores(coins)
    }
    catch (e) {
      throw new Error(e.message)
    }
  }

  /**
   * Getting information about bitcoin or ether addresses
   * @param {Object} data - Type of synchronization method
   * @param {string} data.coin - Coin name for synchronization
   * @param {string} data.type - Coin type. There may be p2pkh or p2wpkh
   * @returns {Promise<Object>} Returns object with bitcoin or ethereum synchronization information
   * @constructor
   */

  async Sync (data) {
    const {coin, type} = data

    try {
      switch (coin) {
        case 'BTC':
          return await this.SyncBTC(type)
        case 'ETH':
          return await this.SyncETH(type)
        case 'BCH':
          return await this.SyncBCH()
        case 'BTCV':
          return await this.SyncBTCV()
      }
    }
    catch (e) {
      throw new Error(e.message)
    }
  }

  /**
   * Getting information about Bitcoin wallet from blockchain
   * @param {string} type - Bitcoin type. There may be p2pkh or p2wpkh
   * @returns {Promise<Object>}
   * @constructor
   */

  async SyncBTC (type = 'p2pkh') {
    if (!this.sync.BTC[type]) {
      this.sync.BTC[type] = new BitcoinSync(
        this.core.COINS.BTC[type].externalNode,
        this.core.COINS.BTC[type].internalNode,
        this.api,
        type
      )
    }

    try {
      await this.sync.BTC[type].Start()
      return this.sync.BTC[type].DATA
    }
    catch (e) {
      console.log('SyncBTC error', e)
    }
  }

  /**
   * Getting information about Ethereum wallet from blockchain
   * @param {number} type - Ethereum account number. By default 0
   * @returns {Promise<Object>}
   * @constructor
   */

  async SyncETH (type = 0) {
    if (!this.sync.ETH[type]) {
      this.sync.ETH[type] = new EthereumSync(this.core.COINS.ETH[type].externalAddress, this.api)
    }

    try {
      await this.sync.ETH[type].Start()
      return this.sync.ETH[type].DATA
    }
    catch (e) {
      console.log('SyncETH error', e)
    }
  }

  /**
   * Getting information about Bitcoin Cash wallet from blockchain
   * @returns {Promise<Object>}
   * @constructor
   */

  async SyncBCH () {
    const type = 'p2pkh'

    if (!this.sync.BCH) {
      this.sync.BCH = new BitcoinCashSync(
        this.core.COINS.BCH[type].externalNode,
        this.core.COINS.BCH[type].internalNode,
        this.api
      )
    }

    try {
      await this.sync.BCH.Start()
      return this.sync.BCH.DATA
    }
    catch (e) {
      console.log('SyncBCH error', e)
    }
  }


  async SyncBTCV () {
    const type = 'p2wpkh'

    if (!this.sync.BTCV) {
      let addresses = {
        external:  this.core.COINS.BTCV[type].externalAddress,
        internal: this.core.COINS.BTCV[type].internalAddress
      }

      this.sync.BTCV = new BitcoinVaultSync(
        this.core.COINS.BTCV[type].externalNode,
        this.core.COINS.BTCV[type].internalNode,
        addresses,
        this.api
      )
    }

    try {
      await this.sync.BTCV.Start()
      return this.sync.BTCV.DATA
    }
    catch (e) {
      console.log('SyncBTCV error', e)
    }
  }

  /**
   * Creating a transaction or getting information about fee
   * @param {Object} data
   * @param {string} data.currency - Transaction currency. It can be of the following types: BTC, ETH, BCH or BTCV
   * @param {string} data.method - Methods 'make' or 'calcFee'
   * @param {Object} data.tx - Input data for the transaction
   * @param {string} data.addressType - Bitcoin address type. It can be p2pkh or p2wpkh (only for BTC)
   * @param {number} data.account - Ethereum account number (only for ETH)
   * @returns {Promise<Object>}
   * @constructor
   */

  async Transaction (data) {
    const {currency, method, tx, addressType, account} = data

    switch (currency) {
      case 'BTC':
        return this.createBTCTx(method, tx, addressType)
      case 'ETH':
        return this.createETHTx(method, tx, account)
      case 'BCH':
        return this.createBCHTx(method, tx)
      case 'BTCV':
        return this.createBTCVTx(method, tx)
      default:
        throw new Error('Unknown txs type (currency)')
    }
  }

  /**
   * Creating a bitcoin transaction
   * @param {string} method - Method 'make' for creating a transaction and method 'calcFee' for calculating fee
   * @param {Object} txData - Input data for the transaction
   * @param {string} addressType - Bitcoin type. There may be p2pkh or p2wpkh
   * @returns {Promise<Object>} Information about the transaction or fee
   */

  async createBTCTx (method, txData, addressType = 'p2pkh') {
    let BTCdata = {
      unspent: this.sync.BTC[addressType].unspent,
      balance: this.sync.BTC[addressType].balance,
      feeList: this.sync.BTC[addressType].fee,
      amount: txData.amount,
      customFee: txData.customFee,
      api: this.api.btc,
      type: addressType
    }

    if (method === 'make') {
      BTCdata.internalAddress = this.sync.BTC[addressType].addresses.empty.internal.address
      BTCdata.nodes = {
        external: this.core.COINS.BTC[addressType].externalNode,
        internal: this.core.COINS.BTC[addressType].internalNode
      }
    }

    let tx = new BitcoinTx(BTCdata)

    switch (method) {
      case 'make':
        return tx.make(txData)
      case 'calcFee':
        return tx.calcFee(txData.size)
      default:
        throw new Error('Unknown btc txs method')
    }
  }

  /**
   * Creating a ethereum transaction
   * @param {string} method - Method 'make' for creating a transaction and method 'calcFee' for calculating fee
   * @param {Object} txData - Input data for the transaction
   * @param {number} account - Ethereum account number. By default 0
   * @returns {Promise<Object>} Information about the transaction or fee
   */

  async createETHTx (method, txData, account = 0) {
    let ETHdata = {
      address: this.sync.ETH[account].address,
      gasPrice: this.sync.ETH[account].gasPrice,
      balance: this.sync.ETH[account].balance,
      privateKey: this.core.COINS.ETH[account].privateKey
    }

    let tx = new EthereumTx(ETHdata)

    switch (method) {
      case 'make':
        return tx.make(txData)
      case 'calcFee':
        return tx.calcFee()
      default:
        throw new Error('Unknown eth txs method')
    }
  }

  async createBCHTx (method, txData) {
    const type = 'p2pkh'

    let BCHdata = {
      unspent: this.sync.BCH.unspent,
      balance: this.sync.BCH.balance,
      feeList: this.sync.BCH.fee,
      amount: txData.amount,
      customFee: txData.customFee
    }

    if (method === 'make') {
      BCHdata.internalAddress = this.sync.BCH.addresses.empty.internal.address
      BCHdata.nodes = {
        external: this.core.COINS.BCH[type].externalNode,
        internal: this.core.COINS.BCH[type].internalNode
      }
    }

    let tx = new BitcoinCashTx(BCHdata)

    switch (method) {
      case 'make':
        return tx.make(txData)
      case 'calcFee':
        return tx.calcFee(txData.size)
      default:
        throw new Error('Unknown BCH txs method')
    }
  }

  async createBTCVTx (method, txData) {
    const type = 'p2wpkh'

    let BTCVdata = {
      unspent: this.sync.BTCV.unspent,
      balance: this.sync.BTCV.balance,
      feeList: this.sync.BTCV.fee,
      amount: txData.amount,
      customFee: txData.customFee
    }

    if (method === 'make') {
      BTCVdata.internalAddress = this.sync.BTCV.addresses.empty.internal.address
      BTCVdata.nodes = {
        external: this.core.COINS.BTCV[type].externalNode,
        internal: this.core.COINS.BTCV[type].internalNode
      }
    }

    let tx = new BitcoinVaultTx(BTCVdata)

    switch (method) {
      case 'make':
        return tx.make(txData)
      case 'calcFee':
        return tx.calcFee(txData.size)
      default:
        throw new Error('Unknown BCH txs method')
    }
  }
}

/**
 * Bitcoin and Ethereum converter
 */

export default {
  /**
   * Convert Satoshi to Bitcoin
   * @param {number} sat
   * @returns {number} btc
   */
  sat_to_btc (sat) {
    if (!+sat) return 0
    return parseFloat((+sat / Math.pow(10, 8)).toFixed(8))
  },
  /**
   * Convert Bitcoin to Satoshi
   * @param {number} btc
   * @returns {number} sat
   */
  btc_to_sat (btc) {
    if (!+btc) return 0
    return Math.round(+btc * Math.pow(10, 8))
  },
  /**
   * Convert WEI to Ethereum
   * @param {number} wei
   * @returns {number} eth
   */
  wei_to_eth (wei) {
    if (!+wei) return 0
    return parseFloat(+wei / Math.pow(10, 18).toFixed(8))
  },
  /**
   * Convert Ethereum to WEI
   * @param {number} eth
   * @returns {number} wei
   */
  eth_to_wei (eth) {
    if (!+eth) return 0
    return Math.round(+eth * Math.pow(10, 18))
  }
}

import * as bip39 from 'bip39'
import * as bitcoin from 'bitcoinjs-lib'
import {Transaction} from 'ethereumjs-tx'
import * as ethUtil from 'ethereumjs-util'
import * as HDkey from 'hdkey'
import * as utils from 'web3-utils'
import wif from 'wif'
import * as bchaddr from 'bchaddrjs'
import * as bitcore from 'bitcore-lib-cash'
import CustomError from '@/helpers/handleErrors'
import {networks} from '@/helpers/networks'

/**
 * Generation of mnemonics.
 * The number of words in a mnemonic depends on the length of the entropy
 * @param {number} entropyLength - The number of bits in the entropy. It can be equal to 128, 160, 192, 224 or 256 bits
 * @returns {string} - A mnemonic whose words are separated by spaces
 */
export function generateMnemonic (entropyLength = 128) {
  try {
    return bip39.generateMnemonic(entropyLength)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_entropy')
  }
}

/**
 * Converting a mnemonic to seed
 * @param {string} mnemonic - Mnemonic phrase
 * @returns {Buffer} - Seed in Uint8Array format
 */
export function mnemonicToSeed (mnemonic) {
  try {
    return bip39.mnemonicToSeedSync(mnemonic)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_mnemonic')
  }
}

/**
 * Converting a mnemonic to entropy
 * @param {string} mnemonic - Mnemonic phrase
 * @returns {string} HEX strings entropy
 */
export function mnemonicToEntropy (mnemonic) {
  try {
    return bip39.mnemonicToEntropy(mnemonic)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_mnemonic')
  }
}

/**
 * Converting a seed to hdkey (Hierarchical Deterministic Key)
 * @param {Buffer} seed - Mnemonic seed in Buffer
 * @returns {Object} hdkey object with private and public key
 */
export function hdFromSeed (seed) {
  try {
    return HDkey.fromMasterSeed(seed)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_seed')
  }
}


/**
 * Converting a xprv to hdkey (Hierarchical Deterministic Key)
 * @param {string} xprv - Extended private key
 * @returns {Object} hdkey object with private and public key
 */
export function hdFromXprv (xprv) {
  try {
    return HDkey.fromExtendedKey(xprv)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_xprv')
  }
}

/**
 * Getting xprv by hdkey
 * @param {Object} hd - HDkey node
 * @returns {string} Extended private key
 */
export function getXprv (hd) {
  try {
    return hd.privateExtendedKey
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_hdkey')
  }
}

/**
 * Derivation of node. Getting child node by path
 * @param {Object} hd - HDkey node
 * @param {string} path - Derivation path
 * @returns {Object} - Child node
 */

export function derive (hd, path) {
  if (!hd) {
    throw new CustomError('err_core_derivation_hdkey')
  }

  if (!path) {
    throw new CustomError('err_core_derivation_path')
  }
  let regex = new RegExp(/(^m\/\d+\')([\/{1}\d+\'{1}]+)/mg)

  if (!regex.test(path)) {
    throw new CustomError('err_core_derivation_path')
  }

  try {
    return hd.derive(path)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_derivation')
  }
}

/**
 * Getting a bitcoin address by node and child index
 * @param {Object} node - HDkey node
 * @param {number} childIndex - Index of the child node
 * @param {string} type - Bitcoin type. There may be p2pkh or p2wpkh
 * @param {string} network - Custom network for different coins
 * @returns {string} Bitcoin address
 */

export function getBtcAddress (node, childIndex = 0, type = 'p2pkh', network = 'btc') {
  const types = ['p2pkh', 'p2wpkh']

  if (!types.includes(type)) {
    throw new CustomError('err_core_btc_type')
  }

  try {
    let pubKey = node.deriveChild(childIndex).publicKey

    return bitcoin.payments[type]({
      pubkey: pubKey,
      network: networks[network] || network.btc
    }).address
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_btc_address')
  }
}

/**
 * Getting an address by public key
 * @param {string} key - Coin public key
 * @param {string} type - Bitcoin type. There may be p2pkh or p2wpkh
 * @param {string} network - Custom network for different coins
 * @returns {string} Bitcoin address
 */

export function getBtcAddressByPublicKey (key, type = 'p2pkh', network = 'btc') {
  if (!key) return ''

  try {
    return bitcoin.payments[type]({
      pubkey: new Buffer(key, 'hex'),
      network: networks[network]
    }).address
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_btc_address')
  }

}

/**
 * Getting a Ethereum private key by node
 * @param {Object} node - Ethereum node
 * @returns {Buffer} Ethereum private key in Uint8Array format
 */

export function getEthPrivateKey (node) {
  try {
    return node._privateKey
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_eth_node')
  }
}

/**
 * Getting a Ethereum public key by private key
 * @param {Buffer} privateKey - Ethereum private key
 * @returns {Buffer} Ethereum public key in Uint8Array format
 */

export function getEthPublicKey (privateKey) {
  try {
    return ethUtil.privateToPublic(privateKey)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_eth_private_key')
  }
}

/**
 * Getting a Ethereum wallet address by public key
 * @param {Buffer} publicKey - Ethereum public key
 * @returns {string} Ethereum wallet address
 */

export function getEthAddress (publicKey) {
  try {
    let addrBuffer = ethUtil
      .publicToAddress(publicKey)
      .toString('hex')
    return ethUtil.toChecksumAddress(addrBuffer).toLowerCase()
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_eth_public_key')
  }
}

/**
 * Getting Ethereum wallet address by node
 * @param {Object} node - Ethereum node
 * @returns {string} Ethereum wallet address
 */

export function getEthAddressByNode (node) {
  try {
    let privateKey = getEthPrivateKey(node)
    let publicKey = getEthPublicKey(privateKey)
    return getEthAddress(publicKey)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_core_eth_public_key')
  }
}

/**
 * Convert a Bitcoin private key to the WIF (Wallet Import Format)
 * @param {Buffer} privateKey - Private key in Uint8Array format
 * @returns {string} Private key in WIF
 */
export function privateKeyToWIF (privateKey) {
  try {
    return wif.encode(128, privateKey, true)
  }
  catch (e) {
    throw new CustomError('err_core_private_key')
  }
}

/**
 * Calculating the transaction size by the number of inputs and outputs
 * @param {number} i - Number of inputs. By default 1
 * @param {number} o - Number of outputs. By default 2
 * @param {boolean} isWitness - Flag signaling that there is a witness in the transaction
 * @returns {number} Transaction size
 */

export function calcBtcTxSize (i = 1, o = 2, isWitness = false) {
  let result = 0

  if (isWitness) {
    let base_size = (41 * i) + (32 * o) + 10
    let total_size = (149 * i) + (32 * o) + 12

    result = ((3 * base_size) + total_size) / 4
  } else {
    result = i * 148 + o * 34 + 10
  }

  return Math.ceil(result)
}

/**
 * Creating a raw Bitcoin transaction
 * @param {Object} data - Input data for a transaction
 * @param {Array} data.inputs - List of inputs
 * @param {Array} data.outputs - List of outputs
 * @returns {Object} Returns raw Bitcoin transaction and transaction hash
 */

export function makeRawBtcTx (data = {}) {
  try {
    const {inputs, outputs} = data
    const psbt = new bitcoin.Psbt()
    let keyPairs = []

    psbt.setVersion(1)

    inputs.forEach(input => {
      const isSegwit = input.address.substring(0, 3) === 'bc1'
      const keyPair = bitcoin.ECPair.fromWIF(input.key)

      keyPairs.push(keyPair)

      let data = {
        hash: input.tx_hash_big_endian,
        index: input.tx_output_n
      }

      if (isSegwit) {
        const p2wpkh = bitcoin.payments.p2wpkh({pubkey: keyPair.publicKey})
        const script = p2wpkh.output.toString('hex')

        data.witnessUtxo = {
          script: Buffer.from(script, 'hex'),
          value: input.value
        }
      } else {
        data.nonWitnessUtxo = Buffer.from(input.tx, 'hex')
      }
      psbt.addInput(data)
    })

    outputs.forEach(output => {
      psbt.addOutput({
        address: output.address,
        value: output.value
      })
    })

    keyPairs.forEach((key, i) => {
      psbt.signInput(i, key)
    })

    psbt.validateSignaturesOfAllInputs()
    psbt.finalizeAllInputs()

    const transaction = psbt.extractTransaction()
    const signedTransaction = transaction.toHex()
    const hash = transaction.getId()

    return {
      hash,
      tx: signedTransaction
    }
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_tx_btc_build')
  }
}

export function makeRawBtcvTx (data = {}) {
  try {
    const {inputs, outputs} = data
    const psbt = new bitcoin.Psbt({ network: networks.btcv })
    let keyPairs = []
    psbt.setVersion(1)

    inputs.forEach(input => {
      const keyPair = bitcoin.ECPair.fromWIF(input.key, networks.btcv)

      keyPairs.push(keyPair)

      let data = {
        hash: input.hash,
        index: input.index
      }


      const p2wpkh = bitcoin.payments.p2wpkh({pubkey: keyPair.publicKey, network: networks.btcv})
      const script = p2wpkh.output.toString('hex')

      data.witnessUtxo = {
        script: Buffer.from(script, 'hex'),
        value: input.value
      }

      psbt.addInput(data)
    })

    outputs.forEach(output => {
      psbt.addOutput({
        address: output.address,
        value: output.value
      })
    })

    keyPairs.forEach((key, i) => {
      psbt.signInput(i, key)
    })

    psbt.validateSignaturesOfAllInputs()
    psbt.finalizeAllInputs()

    const transaction = psbt.extractTransaction()
    const signedTransaction = transaction.toHex()
    const hash = transaction.getId()

    return {
      hash,
      tx: signedTransaction
    }
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_tx_btc_build')
  }
}
/**
 * Creating a raw Ethereum transaction
 * @param {Object} data - Input data for a transaction
 * @param {string} data.to - Recipient address or contract address
 * @param {number} data.value - Transaction amount
 * @param {number} data.nonce - Nonce, transaction count of an account
 * @param {number} data.gasPrice - Transaction gas price
 * @param {number} data.gasLimit - Transaction gas limit
 * @param {string} data.from - Ethereum sender address (required for ERC20 transactions)
 * @param {string} data.data - Data in hex representation (required for ERC20 transactions)
 * @param {string|Buffer} data.privateKey - Ethereum private key in hex or Buffer format
 * @returns {Object} Returns raw Ethereum transaction and transaction hash
 */

export function makeRawEthTx (data = {}) {
  const {to, value, nonce, gasPrice, gasLimit, privateKey, chainId} = data

  if (isNaN(nonce) || isNaN(value) || isNaN(gasPrice) ||
    isNaN(gasLimit)) {
    throw new CustomError('err_tx_eth_invalid_params')
  }

  if (typeof to !== 'string') {
    throw new CustomError('err_tx_eth_invalid_params_string')
  }

  try {
    let params = {
      to: to,
      nonce: utils.toHex(parseInt(nonce)),
      value: utils.toHex(parseInt(value)),
      gasPrice: utils.toHex(parseInt(gasPrice)),
      gasLimit: utils.toHex(parseInt(gasLimit)),
      chainId: utils.toHex(1)
    }

    if (data.hasOwnProperty('from') && data.from) {
      params.from = data.from
    }

    if (data.hasOwnProperty('data') && data.data) {
      params.data = data.data
    }

    const tx = new Transaction(params)
    const privateKeyBuffer = ethUtil.toBuffer(privateKey)

    tx.sign(privateKeyBuffer)

    const serializedTx = tx.serialize()
    const hash = tx.hash().toString('hex')

    return {
      hash: `0x${ hash }`,
      tx: `0x${ serializedTx.toString('hex') }`
    }
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_tx_eth_build')
  }
}

/**
 * Creating a raw Bitcoin Cash transaction
 * @param {Object} data - Input data for a transaction
 * @param {Array} data.inputs - List of inputs
 * @param {Array} data.outputs - List of outputs
 * @returns {Object} Returns raw Bitcoin Cash transaction and transaction hash
 */

export function makeRawBchTx (data = {}) {
  try {
    const {inputs, outputs} = data
    const privateKeys = inputs.map(item => item.key)
    const utxos = inputs.map(item => {
      item.outputIndex = +item.outputIndex
      item.satoshis = +item.satoshis
      item.address = getCashAddress(item.address)

      return item
    })

    const outputsInCashFormat = outputs.map(item => {
      item.address = getCashAddress(item.address)
      item.satoshis = +item.satoshis

      return item
    })
    const tx = new bitcore.Transaction()
      .from(utxos)
      .to(outputsInCashFormat)
      .sign(privateKeys)

    const txData = tx.serialize()

    return {
      tx: txData.toString('hex'),
      hash: tx.hash
    }
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_tx_bch_build')
  }
}

/**
 * Convert a Bitcoin Cash address from Legacy format to CashAddr format
 * @param {string} address - Bitcoin Cash address in Legacy format
 * @returns {string} Returns Bitcoin Cash address in CashAddr format
 */

export function getCashAddress (address = '') {
  try {
    const toCashAddress = bchaddr.toCashAddress

    return toCashAddress(address)
  }
  catch (e) {
    console.log(e)
    throw new CustomError('err_get_bch_address')
  }
}

/**
 * Getting Bitcoin private key for address by derivation index
 * @param {Object} node - Input data for a transaction
 * @param {number} index - Derivation index
 * @returns {string} Returns Private key in WIF format
 */

export function getBtcPrivateKeyByIndex (node, index) {
  try {
    const key = node.deriveChild(index).privateKey

    return privateKeyToWIF(key)
  }
  catch (e) {
    throw new CustomError('err_btc_private_key_by_index')
  }
}

import errors from '@/helpers/errors'

/**
 * Custom error object.
 * By the error code it substitutes the desired message from object 'errors'
 * @extends Error
 */

class CustomError extends Error {
  constructor(code) {
    super(code)
    this.name = 'CustomError'
    this.message = errors.hasOwnProperty(code) ? `${code}: ${errors[code]}` : 'Uncaught error'

    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, this.constructor)
    } else {
      this.stack = (new Error()).stack
    }
  }
}

export default CustomError

